int basis_functions_fourier(double **array, int size, int pmax, double max, double *values){

	int i,j,k,n;
	double p[pmax+1];
	double x = 0;
	
	for (j=0;j<pmax+1;j++){
		k = fmod(j,2);
		n = j+k;
		
		for (i=0;i<size;i++){
			x = (values[i]/max);
			
			if(k==0){
				array[i][j] = cos(M_PI*n*x);
			}else{
				array[i][j] = sin(M_PI*n*x);
			}
			
		}
		
	}
	
	return 0;
}

int basis_functions_sinlog(double *array_flat, int size, int pmax, double xmin, double max, double *values){

	int i,j,k,n;
	double p[pmax+1];
	double x = 0e0;
	double y = 1e0/log(max/xmin);
	double (*restrict array)[pmax+1] = (double (*restrict)[pmax+1]) array_flat;
	
	for (j=0;j<pmax+1;j++){
		k = fmod(j,2);
		n = j+k;
		
		for (i=0;i<size;i++){
			
			if(values[i]<1e-6){
				if(k==0){
					array[i][j] = 1e0;
				}else{
					array[i][j] = 0e0;
				}
			}else{
				x = log(values[i]/xmin)*y;
				
				if(k==0){
					array[i][j] = cos(M_PI*n*x);
				}else{
					array[i][j] = sin(M_PI*n*x);
				}
			}
			
		}
		
	}
	
	return 0;
}

double sinlog_pt(double kpt, int j){

	int n,k;
	double x = 0e0;
	double xmin = 2e0/get_tau0();
	double xmax = eflag_lmax/get_tau0();
	double y = 1e0/log(xmax/xmin);
	double pt;
	
	k = fmod(j,2);
	n = j+k;

	x = log(kpt/xmin)*y;
	
	if(k==0){
		pt = cos(M_PI*n*x);
	}else{
		pt = sin(M_PI*n*x);
	}
	
	return pt;
}

int basis_functions_legendre(double **array, int size, int pmax, double max, double *values){

	int i,j;
	
	double p[pmax+1];
	double x = 0;
	
	for (i=0;i<size;i++){
		x = values[i]/max;
		
		array[i][0] = 1e0;
		if(pmax>0) array[i][1] = 2e0*x-1e0;
		if(pmax>1){
			for (j=2;j<pmax+1;j++){
				array[i][j] = ((2e0*j-1e0)*array[i][1]*array[i][j-1] - (j-1e0)*array[i][j-2] ) / (1e0*j);
			}
		}
	}
	return 0;
}

